mapper.insert:
 
		INSERT INTO notice_board (
			title
			, contents
			, registered_id
			, registered_date
		)VALUES(
			#{title}
			, #{contents}
			, #{registered_id}
			, now()
		)

mapper.update:
update
	notice_board
set
	title = #{title}
	, contents = #{contents}
	, registered_id = #{registered_id}
	, registered_date = now()
	, updated_id = #{registered_id}
	, updated_date = now()
where
	id = #{id}

