mapper.insert:
 
		INSERT INTO notice (
			title
			, contents
			, registered_id
			, registered_date
		)VALUES(
			#{title}
			, #{contents}
			, #{registered_id}
			, now()
		)

mapper.update:
update
	notice
set
	title = #{title}
	, contents = #{contents}
	, registered_id = #{registered_id}
	, registered_date = now()
	, updated_id = #{registered_id}
	, updated_date = now()
where
	id = #{id}
