mapper.select:
SELECT 
	*
FROM
	notice
WHERE 1=1
	
		 and title like #{search}
	
	
		 and registered_id like #{search}
	
	
		 and contents like #{search}
	
ORDER BY ID DESC
LIMIT #{cri.pageStart}, #{cri.perPageNum}

SELECT
	COUNT(*)
FROM 
	notice
WHERE 1=1
	
		 and title like #{search}
	
	
		 and super_id like #{search}
	
	
		 and contents like #{search}

mapper.insert:
 
		INSERT INTO notice (
			title
			, contents
			, admin_id
			, admin_name
			, registered_date
		)VALUES(
			#{title}
			, #{contents}
			, #{admin_id}
			, #{admin_name}
			, now()
		)

mapper.update:
update
	notice
set
	title = #{title}
	, contents = #{contents}
	, admin_id = #{admin_id}
	, registered_date = now()
	, updated_id = #{admin_id}
	, updated_date = now()
where
	id = #{id}

